import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Map from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';

const MAPBOX_TOKEN = 'pk.eyJ1IjoibXVzYS1hcHAiLCJhIjoiY2xnNXBtcGp1MHVvbzNkcGF6Z2E1eHQ5ZyJ9.9p4bW5nKgmBYOEOAQoRPXA';

const MapView = ({
  center = [-74.5, 40],
  zoom = 9,
  markers = [],
  onMarkerClick,
  interactive = true,
  style = { width: '100%', height: '100%' }
}) => {
  const [viewState, setViewState] = useState({
    longitude: center[0],
    latitude: center[1],
    zoom: zoom
  });

  // Aggiorna la vista quando cambiano le props
  useEffect(() => {
    setViewState({
      longitude: center[0],
      latitude: center[1],
      zoom: zoom
    });
  }, [center, zoom]);

  return (
    <Map
      {...viewState}
      onMove={evt => interactive && setViewState(evt.viewState)}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      mapboxAccessToken={MAPBOX_TOKEN}
      style={style}
    >
      {markers.map(marker => (
        <div
          key={marker.id}
          className="marker"
          style={{
            position: 'absolute',
            left: '0',
            top: '0',
            transform: `translate(
              ${viewState.longitude === marker.coordinates[0] && viewState.latitude === marker.coordinates[1] ? '-50%, -50%' : '0, 0'}
            )`,
            width: '24px',
            height: '24px',
            backgroundColor: marker.type === 'artwork' ? '#3B82F6' : marker.type === 'venue' ? '#8B5CF6' : '#10B981',
            borderRadius: '50%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
          }}
          onClick={() => onMarkerClick && onMarkerClick(marker)}
        >
          {marker.type === 'artwork' ? 'A' : marker.type === 'venue' ? 'V' : 'C'}
        </div>
      ))}
    </Map>
  );
};

MapView.propTypes = {
  center: PropTypes.array,
  zoom: PropTypes.number,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      coordinates: PropTypes.array.isRequired,
      title: PropTypes.string,
      type: PropTypes.string
    })
  ),
  onMarkerClick: PropTypes.func,
  interactive: PropTypes.bool,
  style: PropTypes.object
};

export default MapView;
