import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Map from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';

const MAPBOX_TOKEN = 'pk.eyJ1IjoiaXZhbi1mdWNjaGkiLCJhIjoiY21iNmE1eG5zMDc2NDJtc2Eyd3ZwZWE1NCJ9.VGF2pus_hDZCwcXXg6xlsg';

const MapView = ({
  center = [-74.5, 40],
  zoom = 9,
  markers = [],
  onMarkerClick,
  interactive = true,
  style = { width: '100%', height: '100%' }
}) => {
  const [viewState, setViewState] = useState({
    longitude: center[0],
    latitude: center[1],
    zoom: zoom
  });

  // Aggiorna la vista quando cambiano le props
  useEffect(() => {
    setViewState({
      longitude: center[0],
      latitude: center[1],
      zoom: zoom
    });
  }, [center, zoom]);

  return (
    <Map
      {...viewState}
      onMove={evt => interactive && setViewState(evt.viewState)}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      mapboxAccessToken={MAPBOX_TOKEN}
      style={style}
    >
      // Esempio semplificato
{markers.map(marker => {
  // Determina il colore del marker in base al tipo e alla fonte
  let backgroundColor = getColorByType(marker.type);
  let borderColor = marker.source === 'openai' ? '#F59E0B' : '#06B6D4';

  return (
    <div
      key={marker.id}
      className="marker"
      style={{
        backgroundColor,
        borderColor,
        borderWidth: '3px',
        // Altri stili...
      }}
      title={`${marker.title} (${marker.source === 'openai' ? 'AI' : 'UGC'})`}
    >
      {getMarkerLabel(marker.type)}
    </div>
  );
})}
    </Map>
  );
};

MapView.propTypes = {
  center: PropTypes.array,
  zoom: PropTypes.number,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      coordinates: PropTypes.array.isRequired,
      title: PropTypes.string,
      type: PropTypes.string
    })
  ),
  onMarkerClick: PropTypes.func,
  interactive: PropTypes.bool,
  style: PropTypes.object
};

export default MapView;
